import React from "react";
import { useHistory } from "react-router-dom";
import { deleteDeck } from "../utils/api/index";
import Error from "./Error";

export default function DeckRemove({
  deckId,
  decks,
  setDecks,
  error,
  setError,
}) {
  const abortController = new AbortController();
  const history = useHistory();

  function handleRemove(event) {
    event.preventDefault();
    const answer = window.confirm(
      "Remove the deck?\n\nYou will not be able to recover it."
    );
    if (answer) {
      RemoveDeck(deckId, abortController.signal)
        .then(() => {
          const filteredDecks = decks.filter((deck) => deck.id !== deckId);
          setDecks(() => {
            return [...filteredDecks];
          });
        })
        .then(history.push("/"))
        .catch((err) => {
          setError(() => err);
          console.error(err);
        });
    }
  };
  if (error) {
    return <Error setError={setError} />;
  }

  return (
    <div>
      <button className="btn btn-danger float-right" onClick={handleRemove}>
        <span className="oi oi-trash"></span>
      </button>
    </div>
  );
}
